    {
        $this->assertFalse($this->object->hasTable(PHIGRATE_TS_SCHEMA_TBL_NAME));
        $this->object->createSchemaVersionTable();
        $this->assertTrue($this->object->hasTable(PHIGRATE_TS_SCHEMA_TBL_NAME));
