    public function testConstructorWithHelpTaskParameter()
    {
        $parameters = array(
            'monScript.php',
            '-d',
            PHIGRATE_BASE . '/tests/fixtures/config/database.ini',
            '-c',
            PHIGRATE_BASE . '/tests/fixtures/config/application.ini',
            'help',
        );
        try {
            new Phigrate_FrameworkRunner($parameters);
            $this->fail('task not defined');
        } catch (Phigrate_Exception_Argument $e) {
            $msg = 'No task found!';
            $this->assertEquals($msg, $e->getMessage());
        }
        $parameters[] = 'db:version';
        $actual = new Phigrate_FrameworkRunner($parameters);
        $this->assertInstanceOf('Phigrate_FrameworkRunner', $actual);
        $expected =<<<USAGE
Task: \033[36mdb:version\033[0m

It is always possible to ask the framework (really the DB) what version it is
currently at.

This task not take arguments.

USAGE;
        $help = $actual->execute();
        $this->assertNotEmpty($help);
        $this->assertEquals($expected, $help);
    }

    public function testGetLogger()
    {
        $parameters = array(
            'monScript.php',
            '-d',
            PHIGRATE_BASE . '/tests/fixtures/config/database.ini',
            '-c',
            PHIGRATE_BASE . '/tests/fixtures/config/application.ini',
            'ENV=test',
            'db:version',
        );
        $actual = new Phigrate_FrameworkRunner($parameters);
        $this->assertInstanceOf('Phigrate_FrameworkRunner', $actual);
        $this->assertInstanceOf('Phigrate_Logger', $actual->getLogger());
        $this->assertEquals(Phigrate_Logger::DEBUG, $actual->getLogger()->getPriority());

        $parameters = array(
            'monScript.php',
            '-d',
            PHIGRATE_BASE . '/tests/fixtures/config/database.ini',
            '-c',
            PHIGRATE_BASE . '/tests/fixtures/config/application.ini',
            'ENV=test3',
            'db:version',
        );
        try {
            new Phigrate_FrameworkRunner($parameters);
            $this->fail('The variable log directory, in config file, is not writable');
        } catch (Phigrate_Exception_InvalidLog $e) {
            $msg = 'Cannot write to log directory: /usr/bin. Check permissions.';
            $this->assertEquals($msg, $e->getMessage());
        }
        $parameters = array(
            'monScript.php',
            '-d',
            PHIGRATE_BASE . '/tests/fixtures/config/database.ini',
            '-c',
            PHIGRATE_BASE . '/tests/fixtures/config/application.ini',
            'ENV=test4',
            'db:version',
        );
        try {
            new Phigrate_FrameworkRunner($parameters);
            $this->fail('The variable log directory, in config file, does not exists');
        } catch (Phigrate_Exception_InvalidLog $e) {
            $msg = '/tmp/phigrate/logs does not exists.';
            $this->assertEquals($msg, $e->getMessage());
        }
        $parameters = array(
            'monScript.php',
            '-d',
            PHIGRATE_BASE . '/tests/fixtures/config/database.ini',
            '-c',
            PHIGRATE_BASE . '/tests/fixtures/config/application.ini',
            'ENV=production',
            'db:version',
        );
        $actual = new Phigrate_FrameworkRunner($parameters);
        $this->assertInstanceOf('Phigrate_FrameworkRunner', $actual);
        $this->assertInstanceOf('Phigrate_Logger', $actual->getLogger());
        $this->assertEquals(Phigrate_Logger::INFO, $actual->getLogger()->getPriority());
    }

